import React, {Component}  from "react"; 
import './App.css';
import Info from './Info'
class App extends Component {
  constructor(props) {
      // So that this.props is not undefined.
      // Allows child class to inherit
      super(props);
      this.state = {
          persons: [
              {
                  name: "Lovon",
                  number: "(000) 000 0000",
                  dob: "09/04/1",
                  hobby: "Coding",
              },
              {
                  name: "Tom",
                  number: "(980) 365 1798",
                  dob: "09/04/1999",
                  hobby: "Coding",
              },
              {
                  name: "Ken",
                  number: "(980) 365 1797",
                  dob: "09/04/1999",
                  hobby: "Coding",
              },
          ],
      };
  }
  render() {
      return (
          <>
              {this.state.persons.map((person) => {
                  return (
                      <Info
                          key={person.number}
                          name={person.name}
                          number={person.number}
                          dob={person.dob}
                          hobby={person.hobby}
                      />
                  );
              })}
          </>
      );
  }
}

export default App;
